mapper.select:

	select * from usertbl
	
		and userNum = #{userNum}
		and userName = #{userName}
		and userId = #{userId}
		and userBirth = #{userBirth}
		and userMobile = #{userMobile}

mapper.update:
update usertbl
userPwd = #{userPwd}, userAddr = #{userAddr}, userPwd = #{userMobile},
where userNum = #{userNum}
